  jaeger-config.json: |
    {
      "service_name": "nginx-thrift",
      "disabled": false,
      "reporter": {
        "logSpans": false,
        "localAgentHostPort": "jaeger:6831",
        "queueSize": 1000000,
        "bufferFlushInterval": 10
      },
      "sampler": {
        "type": "probabilistic",
        "param": 0.01
      }
    }
  nginx.conf: "# Load the OpenTracing dynamic module.
load_module modules/ngx_http_opentracing_module.so;

#
    Checklist: Make sure that worker_processes == #cores you gave to
# nginx process
worker_processes
    \ auto;

# error_log  logs/error.log;

# Checklist: Make sure that worker_connections
    * worker_processes
# is greater than the total connections between the client
    and Nginx. 
events {
  use epoll;
  worker_connections  1024;
}

env fqdn_suffix;

http
    {
  # Load a vendor tracer
  opentracing on;
  opentracing_load_tracer /usr/local/lib/libjaegertracing_plugin.so
    /usr/local/openresty/nginx/jaeger-config.json;

  include       mime.types;

    \ default_type  application/octet-stream;

  proxy_read_timeout 5000;
  proxy_connect_timeout
    5000;
  proxy_send_timeout 5000;

  log_format main '$remote_addr - $remote_user
    [$time_local] "$request"'
                  '$status $body_bytes_sent "$http_referer"
    '
                  '"$http_user_agent" "$http_x_forwarded_for"';
  # access_log
    \ logs/access.log  main;

  sendfile        on;
  tcp_nopush      on;
  tcp_nodelay
    \    on;

  # Checklist: Make sure the keepalive_timeout is greateer than

    \ # the duration of your experiment and keepalive_requests
  # is greateer than
    the total number of requests sent from
  # the workload generator
  keepalive_timeout
    \ 120s;
  keepalive_requests 100000;

  # Docker default hostname resolver.
    Set valid timeout to prevent unlimited
  # ttl for resolver caching.
  # resolver
    127.0.0.11 valid=10s ipv6=off;
  resolver kube-dns.kube-system.svc.cluster.local
    valid=10s ipv6=off;

  lua_package_path '/usr/local/openresty/nginx/lua-scripts/?.lua;/usr/local/openresty/luajit/share/lua/5.1/?.lua;;';


    \ lua_shared_dict config 32k;

