load_module modules/ngx_http_opentracing_module.so;

#
    Checklist: Make sure that worker_processes == #cores you gave to
# nginx process
worker_processes
    \ auto;

# error_log  logs/error.log;

# Checklist: Make sure that worker_connections
    * worker_processes
# is greater than the total connections between the client
    and Nginx. 
events {
  use epoll;
  worker_connections  1024;
}

env fqdn_suffix;

http
